String.prototype.replaceAll = function(str1, str2, ignore) 
{
	return this.replace(new RegExp(str1.replace(/([\/\,\!\\\^\$\{\}\[\]\(\)\.\*\+\?\|\<\>\-\&])/g,"\\$&"),(ignore?"gi":"g")),(typeof(str2)=="string")?str2.replace(/\$/g,"$$$$"):str2);
}

function getPromise(url) {
  // Return a new promise.
  return new Promise(function(resolve, reject) {
    // Do the usual XHR stuff
    var req = new XMLHttpRequest();
    req.open('GET', url);

    req.onload = function() {
      // This is called even on 404 etc
      // so check the status
      if (req.status == 200) {
        // Resolve the promise with the response text
        resolve(req.response);
      }
      else {
        // Otherwise reject with the status text
        // which will hopefully be a meaningful error
        reject(Error(req.statusText));
      }
    };

    // Handle network errors
    req.onerror = function() {
      reject(Error("Network Error"));
    };

    // Make the request
    req.send();
  });
}

function getJSON(url) {
  return getPromise(url).then(JSON.parse).catch(function(err) {
    console.log("getJSON failed for", url, err);
    throw err;
  });
}


function makeRequest (opts) {
  // modified from http://stackoverflow.com/questions/30008114/how-do-i-promisify-native-xhr
  return new Promise(function (resolve, reject) {
    var xhr = new XMLHttpRequest();
    xhr.open(opts.method, opts.url);
    xhr.onload = function () {
      if (this.status >= 200 && this.status < 300) {
        resolve(xhr.response);
      } else {
        reject({
          status: this.status,
          statusText: xhr.statusText
        });
      }
    };
    xhr.onerror = function () {
    	reject(new Error("unable to complete " + opts.method + " request for: " + opts.url));
    };
    if (opts.headers) {
      Object.keys(opts.headers).forEach(function (key) {
        xhr.setRequestHeader(key, opts.headers[key]);
      });
    }
    var params = opts.params;
    // We'll need to stringify if we've been given an object
    // If we have a string, this is skipped.
    if (params && typeof params === 'object') {
    	//if Content-Type is json, use json stringify instead of other method
    	if(opts.headers && opts.headers.hasOwnProperty("Content-Type") && ~opts.headers["Content-Type"].indexOf("application/json")){
    		params = JSON.stringify(params);
    	}else{
    		params = Object.keys(params).map(function (key) {
    	        return encodeURIComponent(key) + '=' + encodeURIComponent(params[key]);
    	      }).join('&');
    	}
    }
    xhr.send(params);
  });
}

function logRequestError(err) {
	if(err.hasOwnProperty("status")){
		console.error('Error!', "Error Status:", err.status, "Error Text:", err.statusText);
	}else{
		console.error(err);
	}
}


$.fn.scrollView = function (offset, timing) {
	scrollOffset = offset || 0;
	if(timing === undefined){
		timing = 1000
	}
    return this.each(function () {
        $('html, body').animate({
            scrollTop: $(this).offset().top - scrollOffset
        }, timing);
    });
}


function drawCircle(x, y, radius, color, borderColor) {
	context.beginPath();
	context.arc(x, y, radius, 0, 2 * Math.PI, false);
	context.fillStyle = color;
	context.fill();
	context.lineWidth = 5;
	context.strokeStyle = borderColor;
	context.stroke();
}

function drawLine(sx, sy, ex, ey, width) {
	context.beginPath();
	context.moveTo(sx, sy);
	context.lineTo(ex, ey, width);
	context.stroke();
}


function setToArray(set) {
	var array_var = [];
	set.forEach(function(element){array_var.push(element)});
	return array_var;
}

function ajax(url, func) {
	$.ajax({
		url : url,
		dataType : 'json',
		async : false,
		success : function(ct) {
			func(ct);
		}
	});
}

function ajaxRet(url){
	var data;
	ajax(url, function(d){ data = d});
	return data;
}

function ajaxPost(url, data, func) {
	$.ajax({
		type: 'POST',
        url: url,
        datatype : 'json',
        async : false,
        data: JSON.stringify(data),
		success: function(ct) {
			func(ct);
		}
    });
}


function ajaxAsync(url, func) {
	$.ajax({
		url : url,
		dataType : 'json',
		async : true,
		success : function(ct) {
			func(ct);
		}
	});
}

var range = function(start, end, step) {
	// from http://stackoverflow.com/questions/3895478/does-javascript-have-a-range-equivalent
	var range = [];
	var typeofStart = typeof start;
	var typeofEnd = typeof end;

	if (step === 0) {
		throw TypeError("Step cannot be zero.");
	}

	if (typeofStart == "undefined" || typeofEnd == "undefined") {
		throw TypeError("Must pass start and end arguments.");
	} else if (typeofStart != typeofEnd) {
		throw TypeError("Start and end arguments must be of same type.");
	}

	typeof step == "undefined" && ( step = 1);

	if (end < start) {
		step = -step;
	}

	if (typeofStart == "number") {

		while (step > 0 ? end >= start : end <= start) {
			range.push(start);
			start += step;
		}

	} else if (typeofStart == "string") {

		if (start.length != 1 || end.length != 1) {
			throw TypeError("Only strings with one character are supported.");
		}

		start = start.charCodeAt(0);
		end = end.charCodeAt(0);

		while (step > 0 ? end >= start : end <= start) {
			range.push(String.fromCharCode(start));
			start += step;
		}

	} else {
		throw TypeError("Only string and number types are supported");
	}

	return range;

}; 



function addMouseScrollListener(obj, up, down) {
	// from http://www.sitepoint.com/html5-javascript-mouse-wheel/
	this.handler = function(e) {

		var e = window.event || e;
		// old IE support

		var delta = Math.max(-1, Math.min(1, (e.wheelDelta || -e.detail)));
		if (delta > 0) {
			up(delta);
		} else {
			down(delta);
		}
		e.preventDefault();
	};
	if (obj.addEventListener) {
		// IE9, Chrome, Safari, Opera
		obj.addEventListener("mousewheel", handler, false);
		// Firefox
		obj.addEventListener("DOMMouseScroll", handler, false);
	} else {
		// IE 6/7/8
		obj.attachEvent("onmousewheel", handler);
	}
}


var getRelCursorPosition = function(event, obj) {
	// from http://stackoverflow.com/a/5417934
	var objOffset = $(obj).offset();
	var x = event.clientX + document.body.scrollLeft + document.documentElement.scrollLeft - Math.floor(objOffset.left);
	var y = event.clientY + document.body.scrollTop + document.documentElement.scrollTop - Math.floor(objOffset.top) + 1;
	return [x, y];
}; 

    

function addDiv(parentId, childName) {
	var div = d3.select(parentId)
		.append("div")
			.attr("id", childName);
	return div;
};

function addH1(selector, text){
	d3.select(selector).append("h1").text(text);
}

var sort_by = function(field, reverse, primer){
	//from http://stackoverflow.com/questions/979256/sorting-an-array-of-javascript-objects
   var key = function (x) {return primer ? primer(x[field]) : x[field];};

   return function (a,b) {
	  var A = key(a), B = key(b);
	  return ( (A < B) ? -1 : ((A > B) ? 1 : 0) ) * [-1,1][+!!reverse];                  
   };
};

function arrayContains(arr, val){
	return (arr.indexOf(val) > -1);
}


function addSvgSaveButton(buttonId, topSvg, name) {
	name = name || "graph.svg"
	if(!name.endsWith('.svg')){
		name += ".svg";
	}
	d3.select(buttonId).append("a").attr("id", "imgDownload");
	d3.select(buttonId).on(
			"click",
			function() {
				var html = $(
						d3.select(topSvg).attr("version", 1.1).attr("xmlns",
								"http://www.w3.org/2000/svg").node()).clone()
						.wrap('<p/>').parent().html();
				// add the svg information to a and then click it to trigger the
				// download
				var imgsrc = 'data:image/svg+xml;base64,' + btoa(html);
				d3.select("#imgDownload").attr("download", name);
				d3.select("#imgDownload").attr("href", imgsrc);
				var a = $("#imgDownload")[0];
				a.click();
			});
}

function setHeadTitle(title){
	if(!$("title", "head").length){
		$("head").append("<title></title>");
	}
	$("title", "head").html(title);
}

function addGifLoading(divId){
	var divSel = divId;
	if(! divId instanceof d3.selection){
		divSel =  d3.select(divId);
	}
	return divSel.append("img")
		.attr("class", "GifLoadingSymbolGif")
		.attr("width", 100)
		.attr("height", 100)
		
		.style("position", "absolute")
		//.style("opacity", 1)
		.style("top", "50%")
		.style("left", "50%")
		.style("margin-top", "-50px")
		.style("margin-left", "-50px")
		.attr("src", "data:image/gif;base64,");

}

function addDivGifLoading(divId){
	var divSel = divId;
	if(! divId instanceof d3.selection){
		divSel =  d3.select(divId);
	}
	var divRet = divSel
		.append("div")
			.attr("class", "GifLoadingSymbolDiv")
			.style("width", "500px")
			.style("height", "500px")
			.style("background", "#EEEEEE")
			.style("box-shadow", "10px 10px 5px #888888")
			.style("position", "absolute")
			.style("top", "50%")
			.style("left", "50%")
			.style("margin-top", "-250px")
			.style("margin-left", "-250px");
	addGifLoading(divRet);
	return divRet;
}

function prsentDivGifLoading(){
	var divSel = d3.select("body")
		.append("div")
		.style("opacity", 0.8)
		.style("background-color", function(){return "#ccc"})
		.style("position", "fixed")
		.style("width", "100%")
		.style("height", "100%")
		.style("top", "0")
		.style("left", 0)
		//.style("z-index", "1000")
		.attr("id", "PrsentingDivGifLoading");	
	//$("#PrsentingDivGifLoading").css("background-color", "rgba(204,204,204,204)");
	addGifLoading(divSel);
	return divSel;
}

function removeAllDivGifLoading(){
	d3.selectAll("#PrsentingDivGifLoading").remove();
}



